import React, {Component} from 'react';
import { StyleSheet, Text, View, Image, Button, Alert } from 'react-native';

export default function App() {
  let title='Latest moisture content values'
  let titles=['Moisture Content', 'Humidity', 'Temperature']
  let mainImg = {
    uri: 'https://fontmeme.com/temporary/760ffcebb679896e3b0f872f59338cb2.png'
  };

  function getMoisture() {
    const response = {  
      body: 
        fetch('http://ec2-52-14-234-1.us-east-2.compute.amazonaws.com/moisture', {
          method: 'GET'
        })
        .then((response) => response.json())
        .then((responseJson) => {
            console.log(JSON.stringify(responseJson));
            Alert.alert(
              "Moisture", 
              JSON.stringify(responseJson),
              [
                { text: 'OK'}
              ]
            )
        })
        .catch((error) => {
            console.error(error);
        }),
    };
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#EEEEEE',
    alignItems: 'center',
    justifyContent: 'center',
  },

  image: {
    width: 350, 
    height: 300,
    resizeMode: 'contain',
  },

  button: {
    color: '#286e23'
  },
});

return (
  <View style={styles.container}>
    <Image source={mainImg} style={styles.image}/>
    <Button title={titles[0]}  onPress={()=>getMoisture()}/>
    <Button title={titles[1]} color='#286e23' onPress={()=>getMoisture()}/>
    <Button title={titles[2]} color='#286e23' onPress={()=>getMoisture()}/>
  </View>
);
}
